import * as types from "../../actionTypes";
import ReviewApi from "../../../api/ReviewApi";

let actionsFunction = {

  //CRUD METHODS

  // Create review
  createReview: function(review) {
    return function(dispatch) {
      return ReviewApi
        .createReview(review)
        .then(review => {
          dispatch(actionsFunction.createReviewSuccess(review));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createReviewSuccess: function(review) {
    return { type: types.CREATE_REVIEW_SUCCESS, payload: review };
  },


  // Get review
  loadReview: function(id) {
    return function(dispatch) {
      return ReviewApi
        .getOneReview(id)
        .then(review => {
          dispatch(actionsFunction.loadReviewSuccess(review));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadReviewSuccess: function(review) {
    return { type: types.GET_REVIEW_SUCCESS, payload: review };
  },

	
  // Save review
  saveReview: function(review) {
    return function(dispatch) {
      return ReviewApi
        .saveReview(review)
        .then(review => {
          dispatch(actionsFunction.saveReviewSuccess(review));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveReviewSuccess: function(review) {
    return { type: types.UPDATE_REVIEW_SUCCESS, payload: review };
  },


};

export default actionsFunction;
